
public class Vowels {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         char x='a';
        
        if (x=='a'||x=='e'||x=='i'||x=='o'||x=='u') {
            System.out.println(x + " is a vowel");
        } else {
            System.out.println(x + " is a consonant");
        }
    }
	}


